import React from 'react';

import './Chatty.css';

// getting props from info.js
const Chatty = props => {
    //Socket.io for the messaging

    console.log(props);

    const changy = e => {
        e.preventDefault()
        setName(e.target.value)
    }

    return (
        <React.Fragment>
            <div className="Chatty">
                <header>
                    {props.age} 
                </header>

                <p>
                    {props.count}
                    Socket.io ting in here
                </p>

                <p>{props.message}</p>

                <button onClick={props.upper} >upppy</button>

                <form onSubmit={props.nameHandler}>
                    <input type="text" placeholder="New Name" onChange={changy}/>

                    <button>
                        Submit
                    </button>
                </form>


                <p>
                    {props.name}
                </p>
            </div>
        </React.Fragment>
    )
}

export default Chatty;
