import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';


import './Data.css';

const Data = props => {

    const [yourID, setYourID] = useState();
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState([]);

    const socketRef = useRef();

    useEffect(() => {
       socketRef.current = io.connect('/');

       socketRef.current.on("your id", id => {
           setYourID(id);
       })
       console.log("socket connection worked")
       socketRef.current.on("message", (message) => {
        recievedMessage(message);
       })
    }, []);

    function recievedMessage(message) {
        setMessages(oldMsgs => [...oldMsgs, message]);
    }

    // Find a way to have this fuction emit the message to the database, and possibly broadcast it to the whole room.
    function sendMessage(e, content) {
        e.preventDefault();
        const messageObject = {
            body: message,
            id: yourID,
        };
        setMessage("")
        socketRef.current.emit("send message", messageObject);
        // Can't do this since it's outside of the src folder...
        // SaveMsg(messageObject);

        let reqBody = {
            content: message
        }

        fetch("/api/messages", {
            method: "POST",
            // headers: {
            //     "Content-Type": "application/json"
            // },
            body: JSON.stringify(reqBody)
        }).then((res) => {
            return res.json();
            console.log(message);
        }).catch((err) => {
            console.log(err);
        });
    }

    function handleChange(e) {
        setMessage(e.target.value);
    }

    return (
    //Send down the info, render the chat shit
        <React.Fragment>
            <div className="Page">
                <div className="Container">
                    {messages.map((message, index) => {
                        if (message.id === yourID) {
                            return (
                                <div className="MyRow" key={index}>
                                    <div className="MyMessage" >
                                        {message.body}
                                    </div>
                                </div>
                            )
                        }
                        return (
                            <div key={index} style={{ justifyContent: 'flex-start' }} >
                                <div className="PartnerMessage" >
                                    {message.body}
                                </div>
                            </div>
                        )
                    })}
                </div>
            </div>
            <span className="Entry">
                <form onSubmit={sendMessage}>
                    <input 
                    value={message} 
                    onChange={handleChange} placeholder="Say something..." />
                    <button className="Button">
                        Submit
                    </button>
                </form>
            </span>
        </React.Fragment>
    )
}  

export default Data;
