import React from 'react';

import './styleTing.css';

const StyleTing = props => {

    const handleChange = e => {
        e.preventDefault()
        props.setCount(e.target.value)

    }

    const handleSubmit = ( e ) => {
        e.preventDefault()
        const eTarget = e.target.innerText;
        console.log(eTarget)
        props.setCount(eTarget)
    }


    return (
        <React.Fragment>
            <div>
                <div className="Name">
                    {props.name}
                </div>

                <div>
                    <form onSubmit={handleSubmit}>
                        <input type="number" placeholder={props.count} onChange={handleChange}  />
                        <button type="submit" />
                    </form>
                </div>

                <div>                
                    {props.age}
                </div>

                <div>
                    {props.count}
                </div>

                <div>
                    {props.job}               
                </div>
            </div>
        </React.Fragment>
    )
}

export default StyleTing;
